 de valores inconsistentes por NA\n    mutate(across(everything(), ~ ifelse(. %in% c(\"NA\", \"No sabe\", 0, -9999, \"\"), NA, .))) %>%\n    \n    #Unificación de categorías\n    mutate(\n      sexo = case_when(\n        grepl(\"mujer\", tolower(sexo)) ~ \"mujer\",\n        grepl(\"hombre\", tolower(sexo)) ~ \"hombre\",\n        TRUE ~ NA_character_\n      ),\n      ecivil = case_when(\n        grepl(\"casado\", tolower(ecivil)) ~ \"casado(a)\",\n        grepl(\"soltero\", tolower(ecivil)) ~ \"soltero(a)\",\n        grepl(\"separado\", tolower(ecivil)) ~ \"separado(a)\",\n        TRUE ~ NA_character_\n      ),\n      pobreza = case_when(\n        grepl(\"no pobreza\", tolower(pobreza)) ~ \"no pobres\",\n        grepl(\"pobreza\", tolower(pobreza)) ~ \"pobreza\",\n        TRUE ~ NA_character_\n      ),\n      region = case_when(\n        grepl(\"tarapaca\", tolower(region)) ~ \"Región de Tarapacá\",\n        TRUE ~ region\n      )\n    ) %>%\n    \n    #Corrección de anomalías en datos numéricos\n    mutate(\n      edad = ifelse(edad > 110, NA, edad),\n      Ingreso_persona = ifelse(Ingreso_persona > 5000000, NA, Ingreso_persona),\n      Ingreso_hogar = ifelse(Ingreso_hogar > 50000000, NA, Ingreso_hogar)\n    )\n}\n\n#Aplicación de la función a los datasets\ndata_2013 <- limpiar_y_transformar(data_2013)\ndata_2017 <- limpiar_y_transformar(data_2017)\ndata_2022 <- limpiar_y_transformar(data_2022)\n\nsummary(data_2013)\nsummary(data_2017)\nsummary(data_2022)","metadata":{"execution":{"iopub.status.busy":"2024-10-24T01:09:10.090295Z","iopub.execute_input":"2024-10-24T01:09:10.092005Z","iopub.status.idle":"2024-10-24T01:09:24.660055Z"},"trusted":true},"execution_count":5,"outputs":[{"output_type":"display_data","data":{"text/plain":"      Anio         ID_hogar          ID_persona           region         \n Min.   :2013   Min.   :1.101e+10   Length:218491      Length:218491     \n 1st Qu.:2013   1st Qu.:5.802e+10   Class :character   Class :character  \n Median :2013   M